class ToggleLikeView(APIView):
    authentication_classes = [TokenAuthentication]
    permission_classes = [IsAuthenticated]
    def post(self, request, pk):
        try:
            item = DukaLako.objects.get(pk=pk)
            user = request.user

            # Check if the user has liked the item
            is_liked = user in item.liked_by.all()
            
            #hizi ni taarifa za mmiliki wa post
            receiver_email = item.email
            receiver_username = item.username

            if user in item.liked_by.all():
                # User has already liked, so unlike it
                item.Likes -= 1
                item.liked_by.remove(user)
                message = f"{user.username} ameondoa like kwenye post yako."

                # Remove the corresponding notification
                Notification.objects.filter(
                    PostOwner=receiver_username,
                    username=user.username,
                    duka_lako=item.Title,
                    message=f"{user.username} amependa post yako.",
                    Photo=user.profile_image
                ).delete()

            else:
                # User has not liked yet, so like it
                item.Likes += 1
                item.liked_by.add(user)
                message = f"{user.username} amependa post yako."

            
            

                # Create notification
                Notification.objects.create(
                    PostOwner=receiver_username,
                    username=user.username,  # Assuming this is a ForeignKey to the user who posted
                    duka_lako=item.Title,
                    message=message,
                    Photo=user.profile_image,
                    phone=user.phone,
                    email=user.email,
                    Location=user.Location,

                    Maelezo=item.Maelezo,
                    PichaYaPost=item.PichaYaPost,
                    PichaYaPost2=item.PichaYaPost2,
                    PichaYaPost3=item.PichaYaPost3,
                    PichaYaPost4=item.PichaYaPost4,
                    PichaYaPost5=item.PichaYaPost5,
                )

                # Send an email to the user
                #print(f"REEE {receiver_email}")
                
                # subject = "Mfugaji Smart"
                # message = f"Hello {receiver_username}, {message}"
                # from_email = settings.EMAIL_HOST_USER
                # recipient_list = [receiver_email]
                # send_mail(subject, message, from_email, recipient_list, fail_silently=True)

            item.save()
            return Response({"Likes": item.Likes, "isLiked": is_liked, "message": message}, status=status.HTTP_200_OK)

        except DukaLako.DoesNotExist:
            return Response({"error": "Item not found"}, status=status.HTTP_404_NOT_FOUND)
        except Exception as e:
            return Response({"error": str(e)}, status=status.HTTP_500_INTERNAL_SERVER_ERROR)



