        # Check if the product is already in the cart
        existing_cart_item = VyakulaCartItems.objects.filter(cart=cart, product=product).first()
        if existing_cart_item:
            return Response(
                {'error': f'Imeshindikana kuchagua chakula, hiki Chakula tayari ulisha kichagua'},
                status=status.HTTP_400_BAD_REQUEST
            )
            

        price = product.price
        quantity = 1 #data.get('quantity')

        # CustomerFullName = data.get('CustomerFullName')
