    def put(self, request):
        data = request.data
        cart_item = VyakulaCartItems.objects.get(id=data.get('id'))
        quantity = 1 #data.get('quantity')
        cart_item.quantity += quantity
        cart_item.save()
        return Response({'success': 'Item Updated Sccussfully'})



    #TO DELETE ITEM IN A CART
    #Eg:
    #Pass id of the product
    # {
    #     "id":9

    # }
    def delete(self, request):
        user = request.user
        data = request.data
        cart_item = VyakulaCartItems.objects.get(id=data.get('id'))
        cart_item.delete()

        cart = VyakulaCart.objects.filter(user=user, ordered=False).first()
        queryset = VyakulaCartItems.objects.filter(cart=cart)
        serializer = VyakulaCartItemsSerializer(queryset, many=True)

        return Response(serializer.data)




